def get_draft(match):
    #Only works for captains mode games
    draft = match['picks_bans']
    return draft
    
def get_heros_in_order(draft):
    #Returns a dictionary of teams and their picking order. Team 0 is Radiant. Team 1 is Dire.
    heros = {0:[], 1:[]}
    for item in draft:
        #~ print(item)
        if item['is_pick']:
            #~ print(item['team'])
            heros[item['team']].append([item['order'], item['hero_id']])
            #~ print(heros[item['team']])
    return heros

